import React, { Component } from 'react';

export class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startLocation: '',
      endLocation: ''
    };
  }

  handleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  handleSubmit = (event) => {
    event.preventDefault(); // Prevent the default form submission behavior
    const { startLocation, endLocation } = this.state;
    // You can now use startLocation and endLocation variables to perform any actions, such as API calls or further processing
    console.log("Start Location:", startLocation);
    console.log("End Location:", endLocation);
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="startLocation">Starting Point:</label>
          <input 
            type="text" 
            id="startLocation" 
            name="startLocation" 
            placeholder="Enter starting point location" 
            value={this.state.startLocation} 
            onChange={this.handleInputChange} 
          />

          <label htmlFor="endLocation">Ending Point:</label>
          <input 
            type="text" 
            id="endLocation" 
            name="endLocation" 
            placeholder="Enter ending point location" 
            value={this.state.endLocation} 
            onChange={this.handleInputChange} 
          />

          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default Search;
